import React from "react";
import { Education } from "./education";
import { JobExperience } from "./jobExperience";
import { PersonalInformation } from "./personalInformation";

export class ContentDisplay extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      FirstName: null,
      SecondName: null,
      Age: null,
      Country: null,
      // Position: null,
      // Company: null,
      // Duration: null,
      University: null,
      Degree: null,
      Subject: null,
    };
  }

  handleCallback = childData => {
    this.setState({ FirstName: childData });
  };

  handleCallback2 = childData2 => {
    this.setState({ SecondName: childData2 });
  };

  handleCallback3 = childData3 => {
    this.setState({ Age: childData3 });
  };

  handleCallback4 = childData4 => {
    this.setState({ Country: childData4 });
  };

  // handleCallbackE = childDataE => {
  //   this.setState({ Position: childDataE });
  // };

  // handleCallbackE2 = childDataE2 => {
  //   this.setState({ Company: childDataE2 });
  // };

  // handleCallbackE3 = childDataE3 => {
  //   this.setState({ Duration: childDataE3 });
  // };

  handleCallbackEduc = childDataEduc => {
    this.setState({ University: childDataEduc });
  };  

  render() {
    const { FirstName, SecondName, Age, Country } = this.state;
    // const {Position, Company, Duration  } = this.state;
    const {University} = this.state;

    return (
      <div>
        <header className="TopBar">
          <h1>CV-APLICATION</h1>
        </header>
        <section className="inputsGetValueSectionDisplay">
          <section className="PersonalInformation">
            <h1>PersonalInformation</h1>

            <PersonalInformation
              parentCallback={this.handleCallback}
              parentCallback2={this.handleCallback2}
              parentCallback3={this.handleCallback3}
              parentCallback4={this.handleCallback4}
            />
          </section>
          <section className="jobExperience">
            <h1>Job Experience</h1>
            {/* <JobExperience
              parentCallE={this.handleCallbackE}
              parentCallE2={this.handleCallbackE2}
              parentCallE3={this.handleCallbackE3}

            /> */}
          </section>
          <section className="education">
            <h1>Education</h1>
            <Education patentCallBackEdu = {this.handleCallbackEduc}/>
            {University}
          </section>
        </section>
        <footer>
          <section className="displayInputsTopBar">
            <h1>Your CV</h1>
          </section>
          <section className="displayInputsPersonalInformation">
            <section>
              <h2>Your name</h2>
              {FirstName}
            </section>
            <section>
              <h2>Your SecondName</h2>
              {SecondName}
            </section>
            <section>
              <h2>Your Age</h2>
              {Age}
            </section>
            <section>
              <h2>Your Country</h2>
              {Country}
            </section>
          </section>
          {/*space*/}
          <section className="displayInputsJobExperience">
            <section>
              <h2>Your Position</h2>
              {/* {Position} */}
            </section>
            <section>
              <h2>Company Name</h2>
              {/* {Company} */}
            </section>
            <section>
              <h2>The Duration</h2>
              {/* {Duration} */}
            </section>
          </section>
        </footer>
      </div>
    );
  }
}
