import React from "react";

let inputElements = ["Position", "Company", "Duration"];

export class JobExperience extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      Position: "",
      Company: "",
      Duration: "",
    };
  }

  getInputValue(event) {
    const value = event.target.value;
    this.setState({
      ...this.state,
      [event.target.value]: value,
    });
  }

//   onTrigger = event => {
//     this.props.parentCallE(this.state.Position);
//     this.props.parentCallE2(this.state.Company);
//     this.props.parentCallE3(this.state.Duration);

//     event.preventDefault();
//   };

  render() {
    return (
      <div>
        {/* <form onSubmit={this.onTrigger}> */}
          {inputElements.map((input, i) => (
            <li key={i}>
              <input
                type="text"
                name={input}
                value={this.state.input}
                placeholder={input}
                onChange={this.getInputValue.bind(this)}
              />
            </li>
          ))}
          <input className="Button" type="submit" value="Submit" />
        {/* </form> */}
      </div>
    );
  }
}
